import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import {Clothes} from "../shared/models/Clothes";
import {ClothesService} from "../services/clothes/clothes.service";

@Component({
  selector: 'app-cloth-page',
  templateUrl: './cloth-page.component.html',
  styleUrls: ['./cloth-page.component.css']
})
export class ClothPageComponent implements OnInit {

  clothes! : Clothes;
  constructor(private activatedRoute: ActivatedRoute, private clothesService: ClothesService) {
    activatedRoute.params.subscribe((params)=> {
      if(params['id']){
        this.clothes = clothesService.getClothById(params['id']);
      }
    })
  }
  ngOnInit(): void {
  }

}
<div class="container">
  <img [src]="clothes.imageUrl" [alt]="clothes.name">
  <div class="details">
    <div class="header">
      <span class="name">
        {{clothes.name}}
      </span>
      <span class="favourite {{clothes.favourite?'':'not'}}">
        ❤
      </span>
    </div>
    <div class="rating">
      <star-rating
      [value] = 'clothes.stars'
      [totalstars] = "5"
      checkedcolor="red"
      uncheckedcolor="black"
      size="30px"
      [readonly] = "true">
      </star-rating>
    </div>
    <div class="tags">
      <app-tags [clothePageTags]="clothes.tags"></app-tags>
    </div>
  </div>
</div>
