import {Clothes} from "../../shared/models/Clothes";
import {Tag} from "../../shared/models/Tag";


@Injectable({
  providedIn: 'root'
})
export class ClothesService {

  constructor() {
  }

  getAllTags(): Tag[] {

  }

  getAllClothesByTag(tag: string): Clothes[] {
    return tag == "All" ?
      this.getAll() :
      this.getAll().filter(clothes => clothes.tags?.includes(tag));
  }

  getAll(): Clothes[] {
    return [
      {
        id: 1,
