import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import {Clothes} from "../shared/models/Clothes";
import {ClothesService} from "../services/clothes/clothes.service";
import { CartService } from '../services/cart/cart.service';

@Component({
  selector: 'app-cloth-page',
  templateUrl: './cloth-page.component.html',
  styleUrls: ['./cloth-page.component.css']
})
export class ClothPageComponent implements OnInit {

  cloth! : Clothes;
  constructor(private activatedRoute: ActivatedRoute,
              private clothesService: ClothesService,
              private cartService: CartService) {
    activatedRoute.params.subscribe((params)=> {
      if(params['id']){
        this.cloth = clothesService.getClothById(params['id']);
      }
    })
  }
  ngOnInit(): void {
  }

  addToCart(){
    this.cartService.addToCart(this.cloth);
    //this.router.navigateByUrl('/cart-page');
  }

}
<div class="container">
  <img [src]="cloth.imageUrl" [alt]="cloth.name">
  <div class="details">
    <div class="header">
      <span class="name">
        {{cloth.name}}
      </span>
      <span class="favourite {{cloth.favourite?'':'not'}}">
        ❤
      </span>
    </div>
    <div class="rating">
      <star-rating
        [value]='cloth.stars'
        [totalstars]="5"
        checkedcolor="red"
        uncheckedcolor="black"
        size="30px"
        [readonly]="true">
      </star-rating>
    </div>
    <div class="tags">
      <app-tags [clothePageTags]="cloth.tags" justifyContent="start"></app-tags>
    </div>
    <div class="price">
      <span>{{cloth.price|currency}}</span>
    </div>
    <button (click)="addToCart()" routerLink="/cart-page">Add To Cart</button>
  </div>
</div>
