import { Component } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

}
<header>
  <div class="container">
    <a href="#" routerLink="/" class="logo">Store</a>
    <nav>
      <ul>
        <li>
          <a href="#" routerLink="/cart-page">Cart</a>
        </li>
      </ul>
    </nav>
  </div>
</header>
