import { Component, OnInit } from '@angular/core';
import { CartService } from '../services/cart/cart.service';
import {Cart} from "../shared/models/Cart";
import {CartItem} from "../shared/models/CartItem";
import {ClothesService} from "../services/clothes/clothes.service";

@Component({
  selector: 'app-cart-page',
  templateUrl: './cart-page.component.html',
  styleUrls: ['./cart-page.component.css']
})
export class CartPageComponent implements OnInit {
  cart!:Cart;
  constructor(private cartService: CartService, private clothesService: ClothesService ) {
    let cloths = clothesService.getAll();
    cartService.addToCart(cloths[1]);
    cartService.addToCart(cloths[2]);
    cartService.addToCart(cloths[4]);
    this.setCart();
  }

  ngOnInit() {
  }
  removeFromCart(cartItem: CartItem) {
    this.cartService.removeFromCart(cartItem.cloth.id);
    this.setCart();
  }

  changeQuantity(cartItem: CartItem, quantityInString: string) {
    const quantity = parseInt(quantityInString);
    this.cartService.changeQuantity(cartItem.cloth.id, quantity);
    this.setCart();
  }
  setCart(){
    this.cart = this.cartService.getCart();
  }
}
